mapper.select:
select
  *
from
  t_ammeter_usage
where
  ammeter_no = #{ammeterNo}
  and enprNo = #{enprNo}

    and end_time >= #{startLine}
    and end_time <= #{endLine}

select
  sum(ammeter_usage)
from
  t_ammeter_usage
where
  ammeter_no in(
    select
      ammeter_no
    from
      t_ammeter
    where
      c_id = #{cid}
  )
  and enprNo = #{enprNo}

    and end_time >= #{startLine}
    and end_time <= #{endLine}
